function UploadImg() {

  const  handlePicture = (e) => {
        e.preventDefault()
    }
  return (
    <form action="" onSubmit={handlePicture} className="upload-pic">
      <label htmlFor="file">Changer photo profil</label>
      <input type="file" id="file" name="file" accept=".jpg, .jpeg, .png" onChange={(e) => setF(e.target.files[0])}/>
    </form>
  )
}

export default UploadImg
