import React from 'react'
import { useState } from 'react'
import SignUpForm from './SignUpForm'
import SingInForm from './SignInForm'

function Auth(props) {
  const [singUpModal, setSignUpModal] = useState(props.signup)
  const [singInModal, setSignInModal] = useState(props.signin)

  const handleModals = (e) => {
    if (e.target.id === 'register') {
      setSignInModal(false)
      setSignUpModal(true)
    } else if (e.target.id === 'login') {
      setSignUpModal(false)
      setSignInModal(true)
    }
  }
  return (
    <div className="bg-sky-200 w-24 min-w-full text-center p-8 rounded-3xl -ml-20">
      <div className="form-container">
        <ul className='p-4 justify-around'>
          <li
            onClick={handleModals}
            id="register"
            className={singUpModal ? 'active-btn bg-neutral-300 text-white cursor-pointer rounded-lg w-96 p-10 hover:bg-sky-700' : ''}
          >
            Inscription
          </li>
          <li
            onClick={handleModals}
            id="login"
            className={singInModal ? 'active-btn' : ''}
          >
            Connexion
          </li>
        </ul>
        {singUpModal && <SignUpForm />}
        {singInModal && <SingInForm />}
      </div>
    </div>
  )
}

export default Auth
