import React, { useState } from 'react'
import axios from 'axios'
function SignUpForm() {
  const [username, setUsername] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [controlPassword, setControlPassword] = useState('')

  const handleRegister = async (e) => {
    e.preventDefault()
    const terms = document.getElementById('terms')
    const usernameError = document.querySelector('.username.error')
    const emailError = document.querySelector('.email.error')
    const passwordError = document.querySelector('.passord.error')
    const passwordConfirmError = document.querySelector(
      '.password-confirm.error'
    )
    const termsError = document.querySelector('.terms.error')

    passwordConfirmError.innerHTML = ''
    termsError.innerHTML = ''

    if (password !== controlPassword || !terms.checked) {
      if (password !== controlPassword) {
        passwordConfirmError.innerHTML =
          'Les mots de passe ne sont pas identiques'
      }
      if (!terms.checked) {
        termsError.innerHTML = 'Veuillez accepter les conditions'
      }
    } else {
      await axios({
        method: 'post',
        url: `${process.env.REACT_APP_API_URL}register`,

        data: {
          username,
          email,
          password,
        },
      })
    }
  }
  return (
    <form action="" onSubmit={handleRegister} id="sing-up-form">
      <label htmlFor="username">Username</label>
      <br />
      <input
        type="text"
        name="username"
        id="username"
        onChange={(e) => setUsername(e.target.value)}
        value={username}
      />
      <div className="username error"></div>
      <br />
      <label htmlFor="email">Email</label>
      <br />
      <input
        type="text"
        name="email"
        id="email"
        onChange={(e) => setEmail(e.target.value)}
        value={email}
      />
      <div className="email error"></div>
      <br />
      <label htmlFor="password">Password</label>
      <br />
      <input
        type="password"
        name="password"
        id="password"
        onChange={(e) => setPassword(e.target.value)}
        value={password}
      />
      <div className="password error"></div>
      <br />
      <label htmlFor="password-conf">Confirmer Mot de passe</label>
      <br />
      <input
        type="password"
        name="password"
        id="password-conf"
        onChange={(e) => setControlPassword(e.target.value)}
        value={controlPassword}
      />
      <div className="password-confirm error"></div>
      <br />
      <input type="checkbox" id="terms" />
      <label htmlFor="terms">
        j'accepte les{' '}
        <a href="/" target="_blank" rel="noponer noreferrer">
          conditions générale
        </a>
      </label>
      <div className="terms error"></div>
      <br />
      <input type="submit" value="Valider Inscription" />
    </form>
  )
}

export default SignUpForm
